tinyMCE.addI18n('fr_FR.xoops_quote',{
    _XOOPS_EDITOR_TINYMCE4_QUOTE_TOOLTIP : 'Citation',
    _XOOPS_EDITOR_TINYMCE4_QUOTE_SUB : 'Collez le texte que vous voulez citer ci-dessous :',
});

tinymce.PluginManager.add('xoops_quote',function(e) {function t(){

e.windowManager.open({
    title : 'Введите цитату',
    body:{
        type: 'textbox',
        name: 'xoops_quote',
        multiline:!0,
        minWidth:500,
        minHeight:300,
        },

        onSubmit:function(t){
        e.insertContent("<div class='xoopsQuote'>"+t.data.xoops_quote+"</div><br />")
        }}
    )}

e.addButton("xoops_quote",{
    title: 'titre 2',
    tooltip: 'Цитирование',
    image: '/class/xoopseditor/tinymce4/external_plugins/xoops_quote/img/quote.png',
    onclick:t
    })
});
